mapper.select:
-- select C.*,T.type_name typeName from tb_company C,tb_service_relation R,tb_service_type T where C.id=R.company_id and R.service_type_id=T.id
SELECT C.*,GROUP_CONCAT(T.type_name separator'、') type_name FROM tb_company C LEFT JOIN tb_service_relation R ON C.id=R.company_id LEFT JOIN tb_service_type T ON R.service_type_id=T.id
address = #{address}
and C.role_id = 2
group by C.id LIMIT ${start},${pageSize};

select distinct c.*,t.type_name,onum.count_num from tb_company c left join tb_service_relation sr on sr.company_id = c.id left join tb_service_type st on st.id = sr.service_type_id left join tb_service s on s.service_type_id = st.id left join (select C.id cid,GROUP_CONCAT(T.type_name separator'、') type_name from tb_company C left join tb_service_relation R on C.id=R.company_id left join tb_service_type T on R.service_type_id=T.id group by C.id) t on t.cid = c.id left join (select c.id,count(c.id) count_num from tb_order_info o left join tb_company c on c.id = o.company_id where company_order_state_id = 4 group by c.id) onum on onum.id = c.id
s.id = #{serviceId}
ORDER BY count_num desc LIMIT #{start},#{pageSize}
